create table tb_categoria (id int8 generated by default as identity, cor varchar(255), titulo varchar(255), primary key (id));
create table tb_role (id int8 generated by default as identity, authority varchar(255), primary key (id));
create table tb_user (id int8 generated by default as identity, email varchar(255), name varchar(255), password varchar(255), primary key (id));
create table tb_user_role (user_id int8 not null, role_id int8 not null, primary key (user_id, role_id));
create table tb_video (id int8 generated by default as identity, descricao varchar(255), titulo varchar(255), url varchar(255), categoria_id int8, primary key (id));
alter table if exists tb_categoria add constraint UK_3w7hgg579dnf0e3adwaav87bi unique (cor);
alter table if exists tb_categoria add constraint UK_ppr58ss139im6bn3sbg3jsrgk unique (titulo);
alter table if exists tb_user add constraint UK_4vih17mube9j7cqyjlfbcrk4m unique (email);
alter table if exists tb_video add constraint UK_gk1uejpsmkv2svck3l7jw7d4s unique (titulo);
alter table if exists tb_user_role add constraint FKea2ootw6b6bb0xt3ptl28bymv foreign key (role_id) references tb_role;
alter table if exists tb_user_role add constraint FK7vn3h53d0tqdimm8cp45gc0kl foreign key (user_id) references tb_user;
alter table if exists tb_video add constraint FK2b3odso8ggtewt0ijl25lbdy foreign key (categoria_id) references tb_categoria;
